<?phpnamespace frontend\controllers;use Yii;use yii\web\NotFoundHttpException;use frontend\base\BaseFrontController;use common\models\Store;//主页控制器class HomeController extends BaseFrontController{    //首页    public function actionIndex() {        $geohash = Yii::$app->request->get('geohash','');        if (empty($geohash)) {            throw new NotFoundHttpException(Yii::t('yii','geohash不存在'));        }        //根据geohash查询出这个范围内的商家        $stores = Store::getStoresByGeoHash($geohash);        if (empty($stores)) {            throw new NotFoundHttpException(Yii::t('yii','附近不存在商家'));        }        return $this->render('index',[            'stores' => $stores        ]);    }}