<?phpnamespace frontend\controllers;use Yii;use yii\web\NotFoundHttpException;use frontend\base\BaseFrontController;//主页控制器class HomeController extends BaseFrontController{    //首页    public function actionIndex() {        $geohash = Yii::$app->request->get('geohash','');        if (empty($geohash)) {            throw new NotFoundHttpException(Yii::t('yii','geohash不存在'));        }        return $this->render('index');    }}